import MainLayout from "../../layouts/MainLayout";
import styles from './HomePage.module.css';
import Course from "../../components/Course/Course";
import {useSelector} from "react-redux";
import React, {useEffect, useState} from "react";
import axios from "axios";
import bgL from "../../images/anime.jpg";
import BookIcon from "../../components/Icons/coursePageIcons/BookIcon";

function HomePage() {
    const courses = useSelector(state => state.courses);
    const [items, setItems] = useState('');
    const [newItem, setNewItem] = useState('some info');
    useEffect(() => {
        // Fetch data from the server on component mount
        axios.get('http://localhost:5050/api/courses').then((response) => {
            setItems(response.data);
        });
    }, []);

    const handleAddItem = () => {
        // Add a new item to the database
        axios.post('http://localhost:5050/api/courses',
            {
                teacher: String,
                groupName: String,
                bg: String,
                bgPosition: String,
                color: String,
                tasks: [
                    {
                        text: String,
                        date: String,
                        icon: String
                    }
                ]
            }).then((response) => {
            setItems([...items, response.data]);
            setNewItem(
                {
                    "teacher": "Rob Loves",
                    "groupName": "IT-Group G",
                    "bg": `url(${bgL})`,
                    "bgPosition": "top right",
                    "color": "white",
                    "tasks": [
                        {
                            "text": "task1 is to do something. " +
                                "I just need text to test the feature, dont bother yourself reading this, please. " +
                                "Loren ipsum Loren ipsum Loren ipsum Loren ipsum Loren ipsum",
                            "date": "Dec 4",
                            "icon": "BookIcon"
                        }
                    ]
                }
            )
        })
    };
    handleAddItem();
    return (
        <MainLayout>
            <div className={styles.courses}>
                {courses.map((course) => {
                    return (
                        <Course
                            id={course.id}
                            teacher={course.teacher}
                            groupName={course.groupName}
                            bg={course.bg}
                            bgPosition={course.bgPosition}
                            color={course.color}
                        />
                    )
                })}
            </div>
        </MainLayout>
    );
}

export default HomePage;
